mapper.select:
SELECT income_index, rider_id, order_id, income_amount, DATE_FORMAT(income_date, '%Y-%m-%d %W %h:%i %p') AS income_date
FROM deliveryIncome
WHERE rider_id = #{riderId}
AND DATE_FORMAT(income_date, '%m') = #{todaysMonth}
  AND DATE_FORMAT(income_date, '%Y') = #{todaysYear}
ORDER BY income_index DESC

SELECT *
FROM deliveryIncome
WHERE rider_id = #{riderId}
  AND order_id = #{orderId}
